function addToCart() {
  let cart = localStorage.getItem("cart");
  if (cart == null) {
    cart = [];
  } else {
    cart = JSON.parse(localStorage.getItem("cart"));
  }
  cart.push(product[index]);
  localStorage.setItem("cart", JSON.stringify(cart));
  window.location.href = "cart.html";
}
